package limitless

import (
	"io"
	"strings"
)

type LimitlessReader struct {
	base *strings.Reader
}

const default_base_string = ""

func NewLimitlessReader(base string) *LimitlessReader {
	if base == "" {
		base = default_base_string
	}

	return &LimitlessReader{
		base: strings.NewReader(base),
	}
}

func (self *LimitlessReader) Read(b []byte) (int, error) {
	n, err := self.base.Read(b)
	if err == io.EOF {
		self.base.Seek(0, io.SeekStart)
		n, err = self.base.Read(b)
	}
	return n, err
}
